import React, { useState } from 'react'
// import { Navigate } from 'react-router-dom';
import {book} from '../../Dummy-data'

import'./All.css';
export default function All() {
  const [value,setvalue]=useState(book)
  console.log("book",value);
  const type = (type) =>{
    const filterdBooks= book.filter((detail)=>{
      return detail.type.includes(type)
     })
     setvalue(filterdBooks)
     console.log("fbook",filterdBooks);

   }
   const dd = ()=>{
   const[name,setname]=useState("Latest Published Books")
   console.log(name);
   
setname(type)
   }
//    
// const resetname=()=>{
//   setname(book.type)
// }

  return (
    <>

<div className='items'>
  <h2 style={{marginLeft:"10%"}}>{name}</h2>
  <button id="alll" onClick={()=>{setvalue(book), dd()} } class="all0">All</button>
  <button id="all" onClick={()=>{type("horror")}} class="all1">Horror</button>
  <button id="all" onClick={()=>{type("triller")}} class="all2">Triller</button>
  <button id="all" onClick={()=>{type("science fiction")}} class="all3">Science Fiction</button>
  <button id="all" onClick={()=>{type("history")}} class="all4">History</button>

 </div>
    <div class="container">
  
  <div id="demo3" class="carousel slide" data-ride="carousel">
  
  <div class="carousel-inner no-padding my-5">
    <div class="carousel-item active">
     
 
  {value.map((data)=>(
    <div class="col-xs-4 col-sm-4 col-md-4">
 <img class="img-fluid card-img-top" src={data.pic} style={{width:"55%",height:"55%"}}alt=""/>
  
 
  <p>{data.name}</p>
  <p>{data.author}</p>
  <p>{data.cost}$</p>
 </div>

  ))}
 
  
  
     
      
    </div>
  </div>

  
  
</div>
</div>
 



  
  
    </>
  )
}
